window.addEventListener("load",function(){var darkSwitch=document.getElementById("darkSwitch");if(darkSwitch){initTheme(darkSwitch);darkSwitch.addEventListener("change",function(){resetTheme(darkSwitch);});}});function initTheme(darkSwitch){if(localStorage.getItem("darkSwitch")==null&&window.matchMedia&&window.matchMedia('(prefers-color-scheme:dark)').matches){var darkThemeSelected=true;}else{var darkThemeSelected=localStorage.getItem("darkSwitch")!==null&&localStorage.getItem("darkSwitch")==="dark";}darkSwitch.checked=darkThemeSelected;darkThemeSelected?document.body.setAttribute("data-theme","dark"):document.body.removeAttribute("data-theme");}function resetTheme(darkSwitch){if(darkSwitch.checked){document.body.setAttribute("data-theme","dark");localStorage.setItem("darkSwitch","dark");}else{document.body.removeAttribute("data-theme");localStorage.setItem("darkSwitch","");}}